import React, { useState, useEffect } from 'react'
import axios from 'axios'
import convertUnits from 'convert-units'
import Grid from '@material-ui/core/Grid'
import moment from 'moment'
import 'moment/locale/es'
import { useParams } from 'react-router-dom'
import AppFrame from '../components/AppFrame/AppFrame'
import CityInfo from './../components/CityInfo'
import Weather from './../components/Weather'
import WheaterDetails from './../components/WheaterDetails'
import Forecast from './../components/Forecast'
import ForecastChart from './../components/ForecastChart'





export const CityPage = () => {

    const [data, setData] = useState(null)
    const [forecastItemList, setForecastItemList] = useState(null)

    const { city, countryCode } = useParams()

    useEffect(() => {
    
        const getForecast = async () => {

            try {

                const appid = "811bed139763af7e4f3428e936ed75fb"
                const url = `https://api.openweathermap.org/data/2.5/forecast?q=${city},${countryCode}&appid=${appid}`
    
                const { data } = await axios.get(url)
                //console.log(data)

                
                const toCelsius = (temp) => Number(convertUnits(temp).from('K').to('C').toFixed(0))

                const daysAhead = [0, 1, 2, 3, 4, 5]

                const days = daysAhead.map( d => moment().add(d, 'd'))

                const dataAux = days.map(day => {

                    const tempObjArray =  data.list.filter(item => {

                        const dayOfYear = moment.unix(item.dt).dayOfYear()
                        return dayOfYear === day.dayOfYear()
                    })

                    const temps = tempObjArray.map(item => item.main.temp)

                    return ({

                        "dayHour": day.format('ddd'),
                        "min": toCelsius(Math.min(...temps)),
                        "max": toCelsius(Math.max(...temps))
                    })
                })

                setData(dataAux)    

                const interval = [4, 8, 12, 16, 20, 24]

                const forecastItemListAux = data.list
                .filter((item, index) => interval.includes(index))
                .map(item => {
                    return ({
                        hour: moment.unix(item.dt).hour(),
                        weekDay: moment.unix(item.dt).format('dddd'),
                        state: item.weather[0].main.toLowerCase(),
                        temperature: toCelsius(item.main.temp)
                    })
                })

                setForecastItemList(forecastItemListAux)

            } catch (error) {

                console.log("error")
            }
            
        }

        getForecast()

    }, [city, countryCode])
    
    return (
        <AppFrame>
            <Grid container
            justifyContent="space-around"
            direction="column"
            spacing={2}>

                <Grid item container 
                    xs={12}
                    justifyContent="center"
                    alignItems="flex-end">
                    <CityInfo city={"Santiago"} country={"Chile"}/>
                </Grid>

                <Grid container item 
                    xs={12}
                    justifyContent="center">
                    <Weather temperature={10} state="clouds" />
                    <WheaterDetails humidity={80} wind= {10}/>
    
                </Grid>

                <Grid item>
                    { data && <ForecastChart data={data}/> }
                </Grid>

                <Grid item>
                    { forecastItemList && <Forecast forecastItemList = {forecastItemList}/> }
                </Grid>

            </Grid>
        </AppFrame>
        
    )
}

export default CityPage
