import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Grid from '@material-ui/core/Grid'
import { useParams } from 'react-router-dom'
import AppFrame from '../components/AppFrame/AppFrame'
import CityInfo from './../components/CityInfo'
import Weather from './../components/Weather'
import WheaterDetails from './../components/WheaterDetails'
import Forecast from './../components/Forecast'
import ForecastChart from './../components/ForecastChart'


const [city, countryCode] = useParams()

export const CityPage = () => {

    const [data, setData] = useState(null)
    const [forecastItemList, setForecastItemList] = useState(null)

    useEffect(() => {
        setData(dataExample)    
        setForecastItemList(forecastItemListExample)
        
    }, [])
    

    const dataExample = [
        {
            "dayHour": "Jue 18",
            "min": 14,
            "max": 22,
        },
        {
            "dayHour": "Vie 06",
            "min": 18,
            "max": 27,
        },
        {
            "dayHour": "Vie 12",
            "min": 18,
            "max": 28,
        },
        {
            "dayHour": "Vie 18",
            "min": 18,
            "max": 25,
        },
        {
            "dayHour": "Sab 06",
            "min": 15,
            "max": 22,
        },
        {
            "dayHour": "Sab 12",
            "min": 12,
            "max": 19,
        }
    ]

    const forecastItemListExample = [
 
        { weekDay: "Jueves", hour: 18, state: "clear", temperature: 17 },
        { weekDay: "Viernes", hour: 6, state: "clouds", temperature: 18 },
        { weekDay: "Viernes", hour: 12, state: "clear", temperature: 18 },
        { weekDay: "Viernes", hour: 18, state: "clouds", temperature: 19 },
        { weekDay: "S치bado", hour: 14, state: "rain", temperature: 17 },
        { weekDay: "S치bado", hour: 12, state: "rain", temperature: 17 },
    ]

    return (
        <AppFrame>
            <Grid container
            justifyContent="space-around"
            direction="column"
            spacing={2}>

                <Grid item container 
                    xs={12}
                    justifyContent="center"
                    alignItems="flex-end">
                    <CityInfo city={"Santiago"} country={"Chile"}/>
                </Grid>

                <Grid container item 
                    xs={12}
                    justifyContent="center">
                    <Weather temperature={10} state="clouds" />
                    <WheaterDetails humidity={80} wind= {10}/>
    
                </Grid>

                <Grid item>
                    { data && <ForecastChart data={data}/> }
                </Grid>

                <Grid item>
                    { forecastItemList && <Forecast forecastItemList = {forecastItemList}/> }
                </Grid>

            </Grid>
        </AppFrame>
        
    )
}

export default CityPage
