import React, {useState, useCallback, useMemo} from 'react'
import { BrowserRouter as Router,
         Switch,
         Route } from 'react-router-dom'
import WelcomePage from './pages/WelcomePage'
import MainPage from './pages/MainPage'
import CityPage from './pages/CityPage'
import NotFoundPage from './pages/NotFoundPage'

const App = () => {

    const [allWeather, setAllWeather] = useState({})
    const [chartData, setChartData] = useState(null)
    const [forecastItemList, setForecastItemList] = useState(null)


    const onSetAllWeather = useCallback( weatherCity => { 
        setAllWeather(allWeather => {
            return ({ ...allWeather, ...weatherCity })
        
        })
    }, [setAllWeather])

    const onSetChartData = useCallback()

    const onSetForecastItemList = useCallback()

    const actions = useMemo(() => (
        {
            onSetAllWeather,
            setChartData, 
            setForecastItemList
        }
    ), [onSetAllWeather, setChartData, setForecastItemList])

    const data = useMemo(() => (
        {
            allWeather,
            chartData,
            forecastItemList
        }
    ), [allWeather, chartData, forecastItemList])

    return (
        
        <Router>
            <Switch>
                <Route exact path="/">
                    <WelcomePage />
                </Route>
                <Route path="/main">
                    <MainPage data={data} actions={actions} />
                </Route>
                <Route path="/city/:countryCode/:city">
                    <CityPage data={data} actions={actions} />
                </Route>
                <Route>
                    <NotFoundPage />
                </Route>
            </Switch>
        </Router>
          
    )
}

export default App
