import React, {Component} from 'react'

class ErrorBoundary extends Component{

    constructor(props){
        super(props)

        this.state = {
            activo: false
        }

    }

    estaActivo = () =>  this.state.activo ? "Activo" : "No Activo"

    onClickHandler = () => this.setState({ activo: true })

    componentDidMount() {
        console.log("El componente se ha montado")
    }
    
    componentDidUpdate(prevProps, prevState) {
        console.log("Estado previo:", prevProps.activo)
        console.log("Estado nuevo:", this.state.activo)
        console.log("El componente se ha actualizado")
    }
    

    render(){
        return (
        
        <div>
            <button onClick={ this.onClickHandler}>
                Activar
            </button>
            <h1>    
                ErrorBoundary {this.props.saludo} 
                {
                    this.estaActivo()
                }
            </h1>

        </div>
          
        )
    }

}

export default ErrorBoundary
